import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Contact() {
  const [show, setShow] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();  // Initialize useNavigate

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 800) {
        setShow(true);
      } else {
        setShow(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup function
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Display error message and redirect to footer after 2 seconds
    setErrorMessage('Message could not be sent. Please try contacting us directly on any of the platforms here.');
    setTimeout(() => {
      setErrorMessage('');
      navigate('/footer');  // Redirect to footer
    }, 2000);
  };

  return (
    <div id='contact' className={show ? 'show' : ''}>
      <h1 className='contact-header'> CONTACT US TODAY</h1>
      <form onSubmit={handleSubmit}>
        <input id='contact-form-input' type='text' placeholder='Full Name' required />
        <input type='email' placeholder='Type your E-mail' required />
        <textarea placeholder='Write here......' name='message'></textarea>
        <input id='contact-form-submit' type='submit' value='Send' />
      </form>
      {errorMessage && <div className='error-message'>{errorMessage}</div>}
    </div>
  );
}

export default Contact;
