import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function Contact() {
  const [show, setShow] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 800) {
        setShow(true);
      } else {
        setShow(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup function
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simulate an error message for demonstration purposes
    setErrorMessage('Message could not be sent. Please try contacting us directly on any of the platforms here.');

    // Clear error message and navigate after 2 seconds
    setTimeout(() => {
      setErrorMessage('');
      // Redirect to the desired page using the Link component after the error message is deleted
    }, 2000);
  };

  return (
    <div id='contact' className={show ? 'show' : ''}>
      <h1 className='contact-header'> CONTACT US TODAY</h1>
      <form onSubmit={handleSubmit}>
        <input id='contact-form-input' type='text' placeholder='Full Name' required />
        <input type='email' placeholder='Type your E-mail' required />
        <textarea placeholder='Write here......' name='message'></textarea>
        <input id='contact-form-submit' type='submit' value='Send'/>
      </form>
      {errorMessage && <div className='error-message'>{errorMessage}</div>}
      {/* Redirect link */}
      {errorMessage && (
        <p>Redirecting in 2 seconds... or <Link to="/footer">click here</Link></p>
      )}
    </div>
  );
}
